import { Injectable } from '@angular/core';
import { catchError, from, map, Observable, throwError } from 'rxjs';
import { HttpClient , HttpHeaders , HttpErrorResponse } from '@angular/common/http';
import { Ad } from '../model/ad';
import { AuthService } from './auth.service';

@Injectable({
  providedIn: 'root'
})
export class DataService {

  REST_API : string = 'http://127.0.0.1:8000/api/';

  httpHeaders = new HttpHeaders({
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${this.authService.getToken()}`
  });

  constructor(private httpClient : HttpClient,private authService : AuthService) { }

  addTask(data:Ad) : Observable<any>{
    let API_URL = this.REST_API + 'tasks/create';
    return this.httpClient.post(API_URL,data).pipe(catchError(this.handleError));
  }

  getTasks() : Observable<any>{
    const requestOptions = { headers: this.httpHeaders };

    // this.httpHeaders = this.httpHeaders.set('Access-Control-Allow-Origin', '*');
    // this.httpHeaders = this.httpHeaders.set('Access-Control-Allow-Methods', 'GET, POST, PUT, DELETE, OPTIONS');
    // this.httpHeaders = this.httpHeaders.set('Access-Control-Allow-Origin', '*');
    // this.httpHeaders = this.httpHeaders.set('Authorization',`Bearer ${this.authService.getToken()}`);
    // console.log(this.authService.getToken());
    let API_URL = this.REST_API + 'tasks';
    console.log(API_URL);
    return this.httpClient.get(API_URL,requestOptions).pipe(catchError(this.handleError));
  }

  getTask(id : any) : Observable<any>{
    let API_URL = this.REST_API + 'tasks/' + id;
    return this.httpClient.post(API_URL,{ headers : this.httpHeaders }).pipe(map((res:any) => {
      return res || {};
    }),catchError(this.handleError));
  }



  handleError(error : HttpErrorResponse){
    let errorMessage = '';
    if(error.error instanceof ErrorEvent){
      errorMessage = error.error.message;
    }
    else{
      errorMessage = `Error Code : ${error.status}\n Message : ${error.message}`;
    }
    console.log(errorMessage);
    return throwError(errorMessage);
  }

}
