import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  email : string = '';
  password : string = '';
  name : string = '';
  password_confirmation : string = '';

  constructor( private authService : AuthService , private router : Router){
      if (this.authService.isLoggedIn) {
        this.router.navigate(['/']);
    }
  }

 registerUser(){
  if(this.authService.register(this.email , this.password, this.password_confirmation, this.name).subscribe()){
    this.router.navigate(['/']);
  }
 }

}
<div style="width: 50%; margin-left: 25%;margin-top: 5%;">
    <h1 style="text-align: center;">Rigster</h1>
    <form (ngSubmit)="registerUser()" method="post">
        <!-- Name -->
        <div class="form-outline mb-4">
        <label class="form-label" for="name">Name</label>
          <input required type="text" [(ngModel)]="name" [ngModelOptions]="{standalone:true}" id="name" class="form-control" />
        </div>

         <!-- Email input -->
         <div class="form-outline mb-4">
            <label class="form-label" for="email">Email address</label>
              <input type="email" [(ngModel)]="email" [ngModelOptions]="{standalone:true}" id="email" class="form-control" />
            </div>

             <!-- Password -->
        <div class="form-outline mb-4">
            <label class="form-label" for="password">Password</label>
              <input type="password" [(ngModel)]="password" [ngModelOptions]="{standalone:true}" id="password" class="form-control" />
          </div>

           <!-- Password Confirmation -->
        <div class="form-outline mb-4">
          <label class="form-label" for="password_confirmation">Password</label>
            <input type="password" [(ngModel)]="password_confirmation" [ngModelOptions]="{standalone:true}" id="password_confirmation" class="form-control" />
        </div>

        <!-- Submit button -->
        <button type="submit" class="btn btn-primary btn-block mb-4">Sign in</button>

        <!-- Register buttons -->
        <div class="text-center">
          <p>Not a member? <a href="#!">Register</a></p>
          <p>or sign up with:</p>
          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-facebook-f"></i>
          </button>

          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-google"></i>
          </button>

          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-twitter"></i>
          </button>

          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-github"></i>
          </button>
        </div>
      </form>
</div>
