import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent {
  data = {};

  constructor( private authService : AuthService , private router : Router){
      if (this.authService.isLoggedIn) {
        this.router.navigate(['/']);
    }
  }

   email : string = '';
   password : string = '';


  loginUser(){
    if(this.authService.login(this.email , this.password).subscribe()){
      this.router.navigate(['/']);
    }
  }

  getToken(){
    console.log(this.authService.isLoggedIn);
  }
}
<div style="width: 50%; margin-left: 25%;margin-top: 5%;">
    <h1 style="text-align: center;">Login</h1>
    <form (ngSubmit)="loginUser()" method="post">
        <!-- Email input -->
        <div class="form-outline mb-4">
        <label class="form-label" for="email">Email address</label>
          <input type="email" [(ngModel)]="email" [ngModelOptions]="{standalone:true}" id="email" class="form-control" />
        </div>

        <!-- Password input -->
        <div class="form-outline mb-4">
            <label class="form-label" for="password">Password</label>
          <input type="password" [(ngModel)]="password" [ngModelOptions]="{standalone:true}" id="password" class="form-control" />
        </div>

        <!-- 2 column grid layout for inline styling -->
        <div class="row mb-4">
          <div class="col d-flex justify-content-center">
            <!-- Checkbox -->
            <div class="form-check">
                <label class="form-check-label" for="form2Example31"> Remember me </label>
              <input class="form-check-input" type="checkbox" value="" id="form2Example31" checked />
            </div>
          </div>

          <div class="col">
            <!-- Simple link -->
            <a href="#!">Forgot password?</a>
          </div>
        </div>

        <!-- Submit button -->
        <button type="submit" class="btn btn-primary btn-block mb-4">Sign in</button>

        <!-- Register buttons -->
        <div class="text-center">
          <p>Not a member? <a href="#!">Register</a></p>
          <p>or sign up with:</p>
          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-facebook-f"></i>
          </button>

          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-google"></i>
          </button>

          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-twitter"></i>
          </button>

          <button type="button" class="btn btn-link btn-floating mx-1">
            <i class="fab fa-github"></i>
          </button>
        </div>
      </form>
      <button type="button" (click)="getToken()" class="btn btn-primary btn-block mb-4">Sign in</button>

</div>
