import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

interface State {
  page: number;
  pageOffset: number;
}

@Component({
  selector: 'app-pagination',
  templateUrl: './pagination.component.html',
  styleUrls: ['./pagination.component.css']
})
export class PaginationComponent {

  @Output() paginationData = new EventEmitter<{ page: number }>();
  @Input() page: any;
  @Input() length: number = 2;
  @Input() pageOffset: number = 0;

  disableFirst : boolean = false;
  disableLast : boolean = false;
  disableNext : boolean = false;
  disablePrevious : boolean = false;

  constructor() {}
  ngOnInit() {
  }

  getFirstPage() {
    if(this.page != 1){
      this.paginationData.emit({ page: 1 });
    }
    else{
      this.disableFirst=true;
    }
  }

  getNextPage() {
    if(this.pageOffset > this.page){
      this.paginationData.emit({ page: this.page + 1 });
    }
    else{
      this.disableNext=true;
    }
  }

  getPreviousPage() {
    if(this.page > 1){
      this.paginationData.emit({ page: this.page - 1 });
    }
    else{
      this.disablePrevious=true;
    }
  }

  getLastPage() {
    if(this.page != this.pageOffset){
      this.paginationData.emit({ page: this.pageOffset });
    }
    else{
      this.disableLast=true;
    }
  }

}

<div style="display: block;margin-left: auto;margin-right: auto;width:50%;">
    <div style="display: block;margin-left: auto;margin-right: auto;width:50%;">
        <nav aria-label="Page navigation example">
            <ul class="pagination">
              <li class="page-item"><a [disabled]="disableFirst" (click)="getFirstPage()" class="page-link" >First</a></li>
              <li class="page-item"><a  (click)="getPreviousPage()" class="page-link" ><<</a></li>
              <li class="page-item"><a (click)="getNextPage()" class="page-link" >>></a></li>
              <li class="page-item"><a (click)="getLastPage()" class="page-link" >Last</a></li>
            </ul>
          </nav>
    </div>
</div>
