import { Component, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/service/auth.service';
import { DataService } from 'src/app/service/data.service';
import { OnInit } from '@angular/core';
import { PaginationComponent } from '../pagination/pagination.component';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.css']
})
export class TasksComponent {
  @ViewChild(PaginationComponent, {static: true}) paginator: PaginationComponent;
  tasks : any ;

  isLoading = false; // show data table if it is false, here you may use a loader when it is true
  // for pagination
  total: any;
  pageOffset: any;
  pageIndex: any;

  // paginator : any;

  constructor( private authService : AuthService , private router : Router,private dataService : DataService){
      if (!this.authService.isLoggedIn) {
        this.router.navigate(['/login']);
      }
  }

  ngOnInit() {
    this.getTasks();
  }

  getTasks(){
    this.tasks = this.dataService.getTasks().subscribe(data => {
        this.tasks = data.content;
        this.paginator = data.paginator;
    });
  }
}
